import React, { Component } from 'react';
import { Card, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import Loading from './Loading';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoaded: false,
      cat: null,
      categoryGet: null
    };
  }

  getAllCat() {
    axios.get('https://api.chucknorris.io/jokes/categories')
      .then(res => {
        const cat = res.data;
        this.setState({
          cat
        })
        //console.log(res.data)
      })
      .catch(error => this.setState({ error }));
  }

  handleClick(id, e) {
    //alert(id);

    axios.get('https://api.chucknorris.io/jokes/random?category=' + id)
      .then(res => {
        const categoryGet = res.data;
        this.setState({
          categoryGet
        })
        //console.log(res.data)
      })
      .catch(error => this.setState({ error }));
  }


  //fetch api axios
  getUsers() {
    axios.get(`https://api.chucknorris.io/jokes/random`)
      .then(res => {
        const users = res.data;
        this.setState({
          users,
          isLoaded: true,
        })
        //console.log(res.data)
      })
      .catch(error => this.setState({ error, isLoaded: false }));

    this.setState({
      isLoaded: false
    })
  }


  componentDidMount() {
    this.getAllCat()
    this.getUsers();
  }

  render() {
    const { isLoaded, users, cat, categoryGet } = this.state;

    return (
      <div className="App">

        {isLoaded ?
          <Container>
            <Row>
              <Col xs={4} sm={4} md={4} >
                <h2>Choose a Category</h2><br />
                <ul>
                  {cat.map((cats, index) =>
                    <li key={index} data-id={cats.id}><a onClick={this.handleClick.bind(this, cats)} href="#"> {cats}</a></li>
                  )}
                </ul>
              </Col>

              {categoryGet ?

                <Col xs={4} sm={4} md={4} key={categoryGet.id}>
                  <Card>
                    <Card.Img variant="top" src={categoryGet.icon_url} />
                    <Card.Body>
                      <Card.Title>Chuck Norris facts</Card.Title>
                      <Card.Text>
                        {categoryGet.value}<br />
                      </Card.Text>
                      <p>Category: {categoryGet.categories}</p>
                    </Card.Body>

                  </Card>
                </Col>
                :
                <Col xs={4} sm={4} md={4} key={users.id}>
                  <Card>
                    <Card.Img variant="top" src={users.icon_url} />
                    <Card.Body>
                      <Card.Title>Chuck Norris facts</Card.Title>
                      <Card.Text>
                        {users.value}
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Col>

              }
            </Row>
          </Container >

          : <Loading message="Loading..." />
        }




      </div>
    );
  }
}

export default App;
