import React, { Component } from 'react';
import { Card, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import Loading from './Loading';
import './App.css';

// function isSearched(searchTerm) {
//   return function (item) {
//     return !searchTerm || item.value.toLowerCase().includes(searchTerm.toLowerCase());
//   }
// }

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoaded: false,
      //searchTerm: ''
    };
    this.searchValue = this.searchValue.bind(this);
  }

  getCat() {
    axios.get('https://api.chucknorris.io/jokes/categories')
      .then(res => {
        const cat = res.data;
        this.setState({
          cat
        })
        console.log(res.data)
      })
      .catch(error => this.setState({ error, isLoaded: false }));
  }

  //fetch api axios
  getUsers() {
    axios.get(`https://api.chucknorris.io/jokes/random?category=${cat}`)
      .then(res => {
        const users = res.data;
        this.setState({
          users,
          isLoaded: true,
        })
        console.log(res.data)
      })
      .catch(error => this.setState({ error, isLoaded: false }));

    this.setState({
      isLoaded: false
    })
  }


  componentDidMount() {
    this.getCat();
    this.getUsers(cat);
  }

  searchValue(e) {
    this.setState({ searchTerm: e.target.value })
  }

  render() {
    const { isLoaded, users, /*searchTerm*/ } = this.state;

    return (
      <div className="App">

        <Container>
          <Row className="justify-content-center">
            <Col xs={12} md={12}>
              <form className="card card-sm">
                <Card>
                  <Card.Body className=" row no-gutters align-items-center">
                    <Col md="auto">
                      <i className="fas fa-search h4 text-body"></i>
                    </Col>
                    <Col>
                      <input className="form-control form-control-lg form-control-borderless" type="search" placeholder="Search" onChange={this.searchValue} />
                    </Col>
                  </Card.Body>
                </Card>
              </form>
            </Col>
          </Row>
        </Container>

        {isLoaded ?
          <Container>
            <Row>
              {//users.map((user, index) =>
                <Col xs={6} sm={6} md={6} key={users.id}>
                  <Card>
                    <Card.Img variant="top" src={users.icon_url} />
                    <Card.Body>
                      <Card.Title>Chuck Norris</Card.Title>
                      <Card.Text>
                        <p>{users.value} </p>
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Col>//)
              }
            </Row>
            <Row>
              <form style={{ display: 'block', margin: '0 auto' }}>
                <button className="btn btn-lg btn-success" type="submit" onSubmit={this.handleSubmit} value="refresh users">Refresh Users</button>
              </form>
            </Row>
          </Container>
          : <Loading message="Loading..." />}

      </div>
    );
  }
}

export default App;
